            house_height = heights[i]
            profit += house_height ** 2
        return profit

    def check_restrictions(heights):
        for l, r, x in restrictions:
            tallest_house = 0
            for i in range(l - 1, r):
                tallest_house = max(tallest_house, heights[i])
            if tallest_house > x:
                return False
        return True

    def find_max_profit():
        max_profit = 0
        for heights in product(range(h + 1), repeat=n):
            if check_restrictions(heights):
                max_profit = max(max_profit, calculate_profit(heights))
        return max_profit
    from itertools import product
    print(find_max_profit())

