def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(map(int, input().split())))
    ans = float('inf')

    def count_changes(curr_matrix):
        changes = 0
        for i in range(n):
            for j in range(m):
                if curr_matrix[i][j] == 0:
                    changes += 1
        return changes

    def is_palindromic(path):
        i = 0
        j = 0
        while i < n // 2:
            if path[i][j] != path[n - 1 - i][m - 1 - j]:
                return False
            i += 1
            j += 1
        return True

    def generate_paths(row, col, path):
        nonlocal ans
        if row == n:
            if col == m:
                if is_palindromic(path):
                    ans = min(ans, count_changes(path))
                return
        if row < n:
            generate_paths(row + 1, col, path + [[matrix[row][col]]])
        if col < m:
            generate_paths(row, col + 1, path + [[matrix[row][col]]])
    generate_paths(0, 0, [])
    print(ans)


