    grid = [input() for _ in range(n)]
    changed_cells = 0

    def is_functional(grid):
        n = len(grid)
        m = len(grid[0])
        visited = [[False] * m for _ in range(n)]

        def dfs(i, j):
            if i < 0 or i >= n or j < 0 or j >= m or visited[i][j]:
                return False
            visited[i][j] = True
            if i == n - 1 and j == m - 1:
                return True
            if grid[i][j] == 'R':
                return dfs(i, j + 1)
            else:
                return dfs(i + 1, j)
        return dfs(0, 0)

    def find_min_changes(grid):
        nonlocal changed_cells
        n = len(grid)
        m = len(grid[0])
        directions = []
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 'R':
                    directions.append((i, j + 1))
                else:
                    directions.append((i + 1, j))
        queue = []
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 'R':
                    if i == n - 1 and j == m - 1:
                        queue.append((i, j))
                else:
                    if i == 0 and j == m - 1:
                        queue.append((i, j))
        while queue:
            curr_i, curr_j = queue.pop(0)
            changed_cells += 1
            if curr_i == n - 1 and curr_j == m - 1:
                return True
            if curr_i < n - 1 and grid[curr_i + 1][curr_j] == 'R':
                if (curr_i + 1, curr_j) not in queue:
                    queue.append((curr_i + 1, curr_j))
            if curr_i > 0 and grid[curr_i - 1][curr_j] == 'D':
                if (curr_i - 1, curr_j) not in queue:
                    queue.append((curr_i - 1, curr_j))
        return False
    while not is_functional(grid):
        changed = False
        for i in range(len(grid)):
            for j in range(len(grid[i])):
                if grid[i][j] == 'R':
                    if i == len(grid) - 1 and j == len(grid[i]) - 1:
                        grid[i][j] = 'D'
                        changed = True
                    elif i < len(grid) - 1:
                        if grid[i + 1][j] == 'D':
                            grid[i][j] = 'R'
                            changed = True
                elif grid[i][j] == 'D':
                    if i == 0 and j == len(grid[i]) - 1:
                        grid[i][j] = 'R'
                        changed = True
                    elif j < len(grid[i]) - 1:
                        if grid[i][j + 1] == 'R':
                            grid[i][j] = 'D'
