def solve():
    n, m = map(int, input().split())
    s = input()
    reachable = [False] * (n + 1)
    distance = [float('inf')] * (n + 1)
    q = []

    def is_good_square(i):
        if s[i] == '0':
            return True
        else:
            return False

    def calculate_distance(start, end):
        visited = set()
        q = [(start, [start])]
        while q:
            curr, path = q.pop(0)
            if curr == end:
                return path
            if curr in visited:
                continue
            visited.add(curr)
            for move in range(1, m + 1):
                next_square = curr + move
                if next_square <= end:
                    if is_good_square(next_square):
                        q.append((next_square, path + [next_square]))
        return None
    start = 0
    for i in range(n + 1):
        if i == 0:
            if is_good_square(i):
                continue
        if is_good_square(i):
            reachable[i] = True
            distance[i] = 0
            q.append(i)
        else:
            reachable[i] = False
            distance[i] = float('inf')
    shortest_distance = float('inf')
    answer = None
    for i in range(n + 1):
        if reachable[i] and distance[i] < shortest_distance:
            shortest_distance = distance[i]
            answer = calculate_distance(start, i)
        elif reachable[i] and distance[i] == shortest_distance:
            current_answer = calculate_distance(start, i)
            if current_answer is None:
                continue
            if answer is None or ''.join(current_answer) < ''.join(answer):
                answer = current_answer
            elif ''.join(current_answer) == ''.join(answer):
                if ''.join(current_answer) < ''.join(answer):
                    answer = current_answer
    if answer is None:
        print(-1)
    else:
        print(' '.join(map(str, answer)))


solve()

