import heapq


def solve():
    n, m, p = map(int, input().split())
    edges = []
    _ = 0
    while _ < m:
        a, b, c = map(int, input().split())
        edges.append((a, b, c))
        _ += 1
    graph = {i: [] for i in range(1, n)}
    for a, b, c in edges:
        graph[a].append((b, c))
    dist = {i: float('inf') for i in range(1, n)}
    dist[1] = 0
    times = {i: float('inf') for i in range(1, n)}
    times[1] = 0
    pq = [(0, 1, 0)]
    while pq:
        cost, u, time = heapq.heappop(pq)
        if cost > dist[u]:
            continue
        for v, cv in graph[u]:
            new_time = time + 1
            new_cost = cost + cv
            if new_time < times[v]:
                dist[v] = new_cost
                times[v] = new_time
                heapq.heappush(pq, (new_cost, v, new_time))
    max_score = -1
    i = 1
    while i < n:
        score = dist[i] + (times[i] * p - 1) // 2
        max_score = max(max_score, score)
        i += 1
    if max_score == -1:
        print('-1')
    else:
        print(max_score)


solve()

