import heapq


def solve():
    n, m, ta, tb, k = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))
    best_arrival_time = -1
    i = 0
    while i < n:
        for j in range(m):
            if b[j] >= a[i] + ta:
                if best_arrival_time == -1 or b[j] - ta < best_arrival_time:
                    best_arrival_time = b[j] - ta
                break
        i += 1
    if best_arrival_time == -1:
        print('-1')
        return
    flights_to_keep = []
    i = 0
    while i < n:
        if a[i] + ta >= b[0]:
            heapq.heappush(flights_to_keep, (-i, 1))
            i += 1
        else:
            continue
    removed_count = 0
    i = 0
    while i < len(flights_to_keep):
        index, count = heapq.heappushpop(flights_to_keep, (-i, 1))
        removed_count += 1
        if removed_count == k + 1:
            break
        if -index - 1 < n:
            heapq.heappush(flights_to_keep, (-(-index - 1), 1))
        i += 1
    new_a = []
    new_b = []
    i = 0
    while i < n:
        if -1 * flights_to_keep[0][0] != i:
            new_a.append(a[i])
        else:
            new_a.pop()
        i += 1
    i = 0
    while i < m:
        if flights_to_keep[0][0] != i:
            new_b.append(b[i])
        else:
            new_b.pop()
        i += 1
    new_a.sort()
    new_b.sort()
    earliest_arrival = float('inf')
    i = 0
    while i < len(new_a):
        earliest_arrival = min(earliest_arrival, new_a[i] + ta + new_b[i] - ta)
        i += 1
    print(earliest_arrival)


