def solve():
    n, x = map(int, input().split())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def is_leaf(node, edges):
        adj = [[] for _ in range(n + 1)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        return len(adj[node]) <= 1

    def remove_leaf(node, edges):
        removed = set()
        for u, v in edges:
            if node in (u, v) and is_leaf(u, edges) or is_leaf(v, edges):
                removed.add(u)
                removed.add(v)
                return True
        return False

    def check_win(node, edges):
        if remove_leaf(node, edges):
            return True
        for u, v in edges:
            if node in (u, v):
                adj = [[] for _ in range(n + 1)]
                for e_u, e_v in edges:
                    if e_u != node:
                        adj[e_u].append(e_v)
                    if e_v != node:
                        adj[e_v].append(e_v)
                if is_leaf(u, edges) or is_leaf(v, edges):
                    return True
        return False

    def check_lose(node, edges):
        if remove_leaf(node, edges):
            return False
        for u, v in edges:
            if node in (u, v):
                adj = [[] for _ in range(n + 1)]
                for e_u, e_v in edges:
                    if e_u != node:
                        adj[e_u].append(e_v)
                    if e_v != node:
                        adj[e_v].append(e_v)
                if is_leaf(u, edges) or is_leaf(v, edges):
                    return False
        return False

    def play_game(node, edges):
        if check_win(node, edges):
            return "Ayush"
        if check_lose(node, edges):
            return "Ashish"
        temp_edges = []
        for u, v in edges:
            if node in (u, v):
                continue
            temp_edges.append((u, v))
        temp_n = n
        for i in range(n + 1):
            if i != node:
                if i in (u, v):
                    continue
                temp_n -= 1
                temp_edges.append((node, i))
        return play_game(node, temp_edges)

    print(play_game(x, edges))


