from collections import defaultdict


class Sol:

    def __init__(self):
        self.G = defaultdict(set)
        self.n = None
        self.edges = []

    def read(self):
        self.n = int(input())
        for i in range(self.n - 1):
            u, v = map(int, input().split())
            self.edges.append((u, v))
            self.G[u].add(v)
            self.G[v].add(u)

    def find_path(self, x, y):
        visited = {x}
        q = [x]
        path = [x]
        while q:
            v = q.pop(0)
            if v == y:
                return path + [y]
            for u in self.G[v]:
                if u not in visited:
                    visited.add(u)
                    q.append(u)
                    path.append(u)
        return None

    def find_pairs(self):
        paths = []
        for u in range(1, self.n + 1):
            for v in range(1, self.n + 1):
                if u != v:
                    p = self.find_path(u, v)
                    if p:
                        paths.append(p)
        return paths

    def solve(self):
        paths = self.find_pairs()
        best_path1 = None
        best_path2 = None
        max_common = 0
        max_length = 0
        for p1 in paths:
            for p2 in paths:
                if set(p1) & set(p2) >= {'{2}'}:
                    common = len(set(p1) & set(p2))
                    length1 = len(p1)
                    length2 = len(p2)
                    if common > max_common:
                        max_common = common
                        max_length = length1 + length2 - 2 * len(set(p1) & set(p2))
                        best_path1 = p1
                        best_path2 = p2
                    elif common == max_common:
                        if length1 + length2 > max_length:
                            max_length = length1 + length2
                            best_path1 = p1
                            best_path2 = p2
        print(*best_path1, sep=' ')
        print(*best_path2, sep=' ')

