def solve():
    n = int(input())
    streams = []
    for _ in range(n):
        s, d, p = map(int, input().split())
        streams.append((s, d, p))

    def get_overlapping_streams(streams, start, end):
        overlapping_streams = []
        for s, d, p in streams:
            if start <= s < end:
                overlapping_streams.append((s, d, p))
        return overlapping_streams

    def get_non_overlapping_streams(streams):
        non_overlapping_streams = []
        for s, d, p in streams:
            is_new = True
            for ns_s, ns_d, ns_p in non_overlapping_streams:
                if s >= ns_s and s < ns_s + ns_d:
                    is_new = False
                    break
            if is_new:
                non_overlapping_streams.append((s, d, p))
        return non_overlapping_streams

    all_streams = get_non_overlapping_streams(streams)
    max_priority = 0

    def calculate_priority(streams):
        nonlocal max_priority
        if not streams:
            return 0
        max_priority = max(max_priority, sum(p for s, d, p in streams))
        for i in range(len(streams)):
            remaining_streams = streams[:i] + streams[i+1:]
            calculate_priority(remaining_streams)

    calculate_priority(all_streams)
    print(max_priority)


