def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        x, s, t, l = map(int, input().split())
        edges.append((x, s, t + l, t))
    min_time = float('inf')
    adj = [[] for _ in range(n)]
    for x in range(n - 1):
        adj[x].append((x + 1, edges[i][2], edges[i][3]))
        adj[x + 1].append((x, edges[i][2], edges[i][3]))
        i += 1
    dist = {i: float('inf') for i in range(n)}
    dist[0] = 0
    pq = [(0, 0)]
    while pq:
        time, node = heapq.heappop(pq)
        if time > dist[node]:
            continue
        for neighbor, new_time, new_dist in adj[node]:
            if new_time > dist[neighbor]:
                dist[neighbor] = new_time
                heapq.heappush(pq, (new_time, neighbor))
    for i in range(n):
        if dist[i] >= 1800:
            min_time = min(min_time, dist[i])
    if min_time == float('inf'):
        print('impossible')
    else:
        print(min_time)


