from math import atan, pi, cos, sin


def touch(points, avg):
    tx, ty = 0, 0
    for pt in points:
        if pt:
            tx += pt[0]
            ty += pt[1]
    tx /= len(points)
    ty /= len(points)
    return (tx, ty)


def distance(p1, p2):
    return ((p1[0] - p2[0]) ** 2 + (p1[1] - p2[1]) ** 2) ** 0.5


def spread(touches, avg):
    ts = [touch(t) for t in touches]
    d = [((tx - avg[0]) ** 2 + (ty - avg[1]) ** 2) for tx, ty in ts]
    return sum(d) ** 0.5


def rotation(angle, c, o):
    return (atan2(c, o) % pi + pi) % (2 * pi)


def arc(length, angle):
    return length * abs(cos(angle))


def get_grip(img):
    touches = []
    for y in range(15):
        for x in range(30):
            if img[y][x] == 'X':
                touches.append((x, y))
    return touches


def main():
