    img2 = input().split()
    g1 = get_grip(img1[0])
    g2 = get_grip(img2[0])
    avg1 = touch(g1, (0, 0))
    avg2 = touch(g2, (0, 0))
    pan = distance(avg1, avg2)
    spread1 = spread(g1, avg1)
    spread2 = spread(g2, avg2)
    zoom = abs(spread2 - spread1)
    rot = 0
    ts1 = [touch(t) for t in g1]
    ts2 = [touch(t) for t in g2]
    for t1, t2 in zip(ts1, ts2):
        rot += atan2(t2[1] - t1[1], t2[0] - t1[0])
    rot = rotation(rot, spread1, 0)
    rot = (rot + pi) % (2 * pi)
    distances = [pan, zoom, abs(rot)]
    print(len(g1), end=' ')
    print('pan' if distances[0] >= distances[1] else 'zoom' if distances[1] > distances[2
        ] else 'rotate')
    print('clockwise' if rot > 0 else 'counter-clockwise' if rot < 0 else 'none')


