        if b == 0:
            return a
        return gcd(b, a % b)

    def factors(n):
        factors_set = set()
        d = 2
        while d * d <= n:
            if n % d == 0:
                factors_set.add(d)
                factors_set.add(n // d)
            d = d + 1
        if n > 1:
            factors_set.add(n)
        return factors_set

