        visited = [False] * n
        for i in range(n):
            if not visited[i]:
                if not dfs(i, -1, arr, visited):
                    return False
        return True

    def dfs(node, parent, arr, visited):
        visited[node] = True
        for i in range(n):
            if arr[i] % students[node] == 0 and i != parent:
                if not visited[i]:
                    if not dfs(i, node, arr, visited):
                        return False
        return True
    count = 0
    i = 0
    while i < 1 << n:
        arr = []
        for j in range(n):
            if i >> j & 1:
