def solve():
    N = int(input())
    A = []
    for _ in range(N - 1):
        u, v = map(int, input().split())
        A.append((u, v))
    M = int(input())
    B = []
    for _ in range(M - 1):
        u, v = map(int, input().split())
        B.append((u, v))

    def get_transmission_cost(A, B):
        C = N + M
        adj = [[] for _ in range(C)]
        for u, v in A:
            adj[u].append(v + N)
            adj[v + N].append(u)
        for u, v in B:
            adj[u].append(v + N + M)
            adj[v + N + M].append(u)
        cost = 0
        for i in range(1, C):
            for j in range(i + 1, C):
                u = i
                v = j
                path = []
                q = [u]
                visited = {u}
                while q:
                    curr = q.pop(0)
                    path.append(curr)
                    for neighbor in adj[curr]:
                        if neighbor not in visited:
                            visited.add(neighbor)
                            q.append(neighbor)
                cost += (path[-1] - path[0]) ** 2
        return cost

    min_cost = float('inf')
    for i in range(1, N + 1):
        for j in range(1, M + 1):
            A_temp = A[:]
            B_temp = B[:]
            temp_A = []
            temp_B = []
            for x in A_temp:
                if x[0] != i:
                    temp_A.append(x)
                if x[1] != i:
                    temp_A.append(x)
            for x in B_temp:
                if x[0] != j:
                    temp_B.append(x)
                if x[1] != j:
                    temp_B.append(x)
            A = temp_A
            B = temp_B
            cost = get_transmission_cost(A, B)
            min_cost = min(min_cost, cost)
    print(min_cost)


