def solve():
    n = int(input())
    edges = []
    for _ in range(2 * (n - 1)):
        u, v = map(int, input().split())
        edges.append((u, v))

    def is_bipartite(graph):
        colors = [-1] * len(graph)
        for i in range(len(graph)):
            if colors[i] == -1:
                queue = [i]
                colors[i] = 0  # 0 for left, 1 for right
                while queue:
                    node = queue.pop(0)
                    for neighbor in graph[node]:
                        if colors[neighbor] == -1:
                            colors[neighbor] = 1 - colors[node]
                            queue.append(neighbor)
                        elif colors[neighbor] == colors[node]:
                            return False
        return True

    def get_graph(edges):
        n = len(edges) // 2
        graph = [[] for _ in range(n)]
        for i in range(len(edges)):
            u, v = edges[i]
            graph[u - 1].append(v - 1)
            graph[v - 1].append(u - 1)
        return graph

    left_edges = []
    right_edges = []
    for i in range(len(edges)):
        u, v = edges[i]
        if (u + v) % 2 == 1:
            left_edges.append(edges[i])
        else:
            right_edges.append(edges[i])

    left_graph = get_graph(left_edges)
    right_graph = get_graph(right_edges)

    if is_bipartite(left_graph) and is_bipartite(right_graph):
        print("".join("LR" if (u + v) % 2 == 1 else "LL" for u, v in edges))
    else:
        print("impossible")

