    n, m = map(int, input().split())
    grid = [input() for _ in range(n)]

    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < m

    def check_adjacent(x, y):
        return (x > 0 and grid[x-1][y] == '#') or (x < n-1 and grid[x+1][y] == '#') or (y > 0 and grid[x][y-1] == '#') or (y < m-1 and grid[x][y+1] == '#')

    def place_coffee_shop(x, y):
        grid[x][y] = 'E'

    def backtrack():
        for x in range(n):
            for y in range(m):
                if grid[x][y] == '#':
                    if not check_adjacent(x, y):
                        return
                    else:
                        place_coffee_shop(x, y)
                        backtrack()
                        grid[x][y] = '#'
                        place_coffee_shop(x, y)
    backtrack()

    for x in range(n):
        for y in range(m):
            if grid[x][y] == '.':
                place_coffee_shop(x, y)
    for row in grid:
        print(''.join(row))

