def solve():
    n, k = map(int, input().split())
    s = input()
    k = int(k)

    def calculate_cost(subsequence):
        return n - len(subsequence)

    def find_subsequences(string, k):
        subsequences = []
        generate_subsequences(string, k, "", subsequences)
        return subsequences

    def generate_subsequences(string, remaining_k, current_subsequence, subsequences):
        if remaining_k == 0:
            if len(current_subsequence) > 0:
                subsequences.append(current_subsequence)
            return
        for i in range(len(string)):
            new_subsequence = current_subsequence + string[i:]
            if len(new_subsequence) <= len(current_subsequence):
                if remaining_k > 0:
                    generate_subsequences(string[i + 1:], remaining_k - 1,
                        new_subsequence, subsequences)
    subsequences = find_subsequences(s, k)
    if len(subsequences) == 0:
        print("-1")
        return
    total_cost = float('inf')
    for sub in subsequences:
        cost = calculate_cost(sub)
        if cost < total_cost:
            total_cost = cost
    print(total_cost)


