def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    adj = [[] for _ in range(n + 1)]
    for u, v in edges:
        adj[u].append(v)

    leaves = []
    for i in range(1, n + 1):
        if not adj[i]:
            leaves.append(i)

    def calculate_max_result():
        max_result = 0
        for leaf in leaves:
            max_result = max(max_result, leaf)
        return max_result

    def calculate_min_result():
        min_result = float('inf')
        for leaf in leaves:
            min_result = min(min_result, leaf)
        return min_result

    def calculate_max_result_recursive(node, visited):
        max_result = 0
        for child in adj[node]:
            if not visited[child]:
                visited[child] = True
                max_result = max(max_result, calculate_max_result_recursive(child, visited))
        return max_result

    def calculate_min_result_recursive(node, visited):
        min_result = float('inf')
        for child in adj[node]:
            if not visited[child]:
                visited[child] = True
                min_result = min(min_result, calculate_min_result_recursive(child, visited))
        return min_result

    max_result = calculate_max_result_recursive(1, [False] * (n + 1))
    min_result = calculate_min_result_recursive(1, [False] * (n + 1))

    print(max_result, min_result)


