        for i in range(n):
            if c[i] != t[i]:
                return False
        return True

    def transform(c):
        new_c = [0] * n
        for i in range(1, n - 1):
            new_c[i] = c[i + 1] + c[i - 1] - c[i]
        return new_c

    def find_transformations(c, t):
        for i in range(n):
            if c[i] != t[i]:
                for j in range(i + 1, n - i):
                    new_c = transform(c)
                    if check_transformations(new_c, t):
                        return True
        return False

    if find_transformations(c, t):
        print('Yes')
    else:
        print('No')


