def solve():
    n, k = map(int, input().split())

    if k > n * n:
        print('-1')
        return

    matrix = [[0] * n for _ in range(n)]

    def is_symmetrical(matrix):
        for i in range(n):
            for j in range(i + 1, n):
                if matrix[i][j] != matrix[j][i]:
                    return False
        return True

    def is_lexicographically_maximal(matrix):
        for i in range(n):
            for j in range(i + 1, n):
                if matrix[i][j] > matrix[j][i]:
                    return False
        return True

    def generate_matrices(current_matrix, current_k, current_n):
        if current_k == 0:
            if is_symmetrical(current_matrix) and is_lexicographically_maximal(current_matrix):
                return [current_matrix]
            else:
                return []

        result = []
        for i in range(current_n):
            for j in range(i + 1, current_n):
                new_matrix = [row[:] for row in current_matrix]
                new_matrix[i][j] = 1
                new_matrix[j][i] = 1
                result.extend(generate_matrices(new_matrix, current_k - 1, current_n))
        return result

    matrices = generate_matrices([[0] * n for _ in range(n)], k, n)

    if not matrices:
        print('-1')
        return

    max_matrix = matrices[0]
    for matrix in matrices:
        if is_symmetrical(matrix) and is_lexicographically_maximal(matrix) and len(matrix) > len(max_matrix):
            max_matrix = matrix

    print('\n'.join([' '.join(map(str, row)) for row in max_matrix]))


solve()

