from collections import deque


def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, w = map(int, input().split())
        edges.append((u, v, w))

    adj = [[] for _ in range(n)]
    for u, v, w in edges:
        adj[u].append((v, w))
        adj[v].append((u, w))

    def bfs(start, end, r):
        q = deque([(start, 0)])
        visited = {start}
        while q:
            u, dist = q.popleft()
            if u == end:
                return dist
            for v, w in adj[u]:
                if w <= r and v not in visited:
                    visited.add(v)
                    q.append((v, dist + 1))
        return -1

    min_range = float('inf')
    for start in range(n):
        for end in range(start + 1, n):
            r = bfs(start, end, 1)
            if r != -1:
                min_range = min(min_range, r)

    if min_range == float('inf'):
        print('IMPOSSIBLE')
    else:
        print(min_range)


solve()

