                if new_pos not in reachable:
                    reachable.add(new_pos)
                    q.append((new_pos, jump_count + 1))

            if pos - b >= 0:
                new_pos = pos - b
                if new_pos not in reachable:
                    reachable.add(new_pos)
                    q.append((new_pos, jump_count + 1))

        return len(reachable)

