    n, h, m = map(int, input().split())
    restrictions = []
    for _ in range(m):
        l, r, x = map(int, input().split())
        restrictions.append((l, r, x))

    dp = {}

    def calculate_max_profit(i, current_height):
        if (i, current_height) in dp:
            return dp[(i, current_height)]

        if i == n + 1:
            return 0

        max_profit = 0

        for height in range(1, h + 1):
            if height <= current_height:
                max_profit = max(
                    max_profit,
                    height**2 +
                    calculate_max_profit(i + 1, current_height - height))

        for restriction_l, restriction_r, restriction_x in restrictions:
            if i >= restriction_l and i <= restriction_r:
                max_profit = max(
                    max_profit,
                    calculate_max_profit(i + 1, current_height) +
                    restriction_x**2)

        dp[(i, current_height)] = max_profit
        return max_profit

    print(calculate_max_profit(1, h))


