    min_changes = float('inf')

    for i in range(1 << n):
        changes = 0
        for j in range(n):
            if (i >> j) & 1:
                changes += 1
        if changes % 2 != 0:
            changes += 1

        is_palindrome = True
        for j in range(n):
            row = []
            for k in range(m):
                if (i >> j) & 1:
                    row.append(matrix[j][k])
                else:
                    row.append(1 - matrix[j][k])
            if row != row[::-1]:
                is_palindrome = False
                break

        if is_palindrome:
            min_changes = min(min_changes, changes)

    print(min_changes)


