            if board[i][j] == 'W':
                white_pawns.append((i, j))
            elif board[i][j] == 'B':
                black_pawns.append((i, j))

    def check_win_for_pawn(pawn):
        if pawn[0] == 0:
            return True
        if pawn[0] == 7:
            return False
        if pawn[1] == 0:
            return True
        if pawn[1] == 7:
            return False

        if pawn[0] == 0:
            for j in range(8):
                if board[pawn[0] + 1][j] == '.':
                    return True
        elif pawn[0] == 7:
            for j in range(8):
                if board[pawn[0] - 1][j] == '.':
                    return True
        elif pawn[1] == 0:
            for i in range(8):
                if board[i][pawn[1] + 1] == '.':
                    return True
        elif pawn[1] == 7:
            for i in range(8):
                if board[i][pawn[1] - 1] == '.':
                    return True

        return False

    for pawn in white_pawns:
        if check_win_for_pawn(pawn):
            return 'A'

