    my_config = []
    for _ in range(n):
        my_config.append(list(map(int, input().split())))

    def is_valid_path(path):
        current_room = 1
        for move in path:
            if move == 1:
                if my_config[current_room - 1][0] != 0:
                    current_room = my_config[current_room - 1][0]
                else:
                    return False
            elif move == 2:
                if my_config[current_room - 1][1] != 0:
                    current_room = my_config[current_room - 1][1]
                else:
                    return False
            elif move == 3:
                if my_config[current_room - 1][2] != 0:
                    current_room = my_config[current_room - 1][2]
                else:
                    return False
            elif move == 4:
                if my_config[current_room - 1][3] != 0:
                    current_room = my_config[current_room - 1][3]
                else:
                    return False
            else:
                return False
        return current_room == n

    def find_all_paths(current_room, path, visited):
        if current_room == n:
            if is_valid_path(path):
                return [path]
            else:
                return []
        paths = []
        for move in range(1, 5):
            if move == 1 and my_config[current_room - 1][0] != 0:
                paths.extend(find_all_paths(my_config[current_room - 1][0], path + [move], visited | {current_room}))
            elif move == 2 and my_config[current_room - 1][1] != 0:
