            elif move == 3 and my_config[current_room - 1][2] != 0:
                paths.extend(find_all_paths(my_config[current_room - 1][2], path + [move], visited | {current_room}))
            elif move == 4 and my_config[current_room - 1][3] != 0:
                paths.extend(find_all_paths(my_config[current_room - 1][3], path + [move], visited | {current_room}))
        return paths

    paths = find_all_paths(1, [1], {1})
