def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    a.sort()
    b.sort()

    ans = float('inf')

    for i in range(1 << m):
        subset = []
        for j in range(m):
            if (i >> j) & 1:
                subset.append(b[j])

        count = 0
        for x in a:
            if x in subset:
                count += 1

        if count >= n:
            ans = min(ans, len(subset) - n + 1)

    print(ans)


