def solve():
    n, m, k = map(int, input().split())
    maze = []
    for _ in range(n):
        maze.append(input())

    x_coords = []
    y_coords = []
    for i in range(n):
        for j in range(m):
            if maze[i][j] == 'X':
                x_coords.append(i)
                y_coords.append(j)

    x = x_coords[0]
    y = y_coords[0]

    directions = ['L', 'R', 'U', 'D']
    min_path = None

    def find_path(current_x, current_y, current_path, visited):
        nonlocal min_path

        if len(current_path) == k:
            if current_x == x and current_y == y:
                if min_path is None or current_path < min_path:
                    min_path = current_path
            return

        for direction in directions:
            new_x = current_x
            new_y = current_y

            if direction == 'L':
                new_x -= 1
            elif direction == 'R':
                new_x += 1
            elif direction == 'U':
                new_y -= 1
            elif direction == 'D':
                new_y += 1

            if 0 <= new_x < n and 0 <= new_y < m and maze[new_x][new_y] != '*':
                if (new_x, new_y) not in visited:
                    visited.add((new_x, new_y))
                    find_path(new_x, new_y, current_path + direction, visited)
                    visited.remove((new_x, new_y))

    visited = set()
    visited.add((x, y))
    find_path(x, y, '', visited)

    if min_path is None:
        print('IMPOSSIBLE')
    else:
        print(''.join(min_path))


solve()

