def solve():
    n = int(input())
    segments = []
    for _ in range(n):
        l, r = map(int, input().split())
        segments.append((l, r))

    adj = [[] for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            l1, r1 = segments[i]
            l2, r2 = segments[j]
            if (l1 <= r2 and l2 <= r1) or (l1 >= r2 and l2 >= r1):
                adj[i].append(j)
                adj[j].append(i)

    def is_tree(graph):
        if len(graph) != n - 1:
            return False

        visited = [False] * n
        stack = [0]
        visited[0] = True

        while stack:
            node = stack.pop()
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    stack.append(neighbor)

        return len(visited) == n

    if is_tree(adj):
        print('YES')
    else:
        print('NO')


solve()

