def solve():
    n = int(input())
    x = list(map(int, input().split()))
    edges = []
    for _ in range(n - 1):
        edges.append(list(map(int, input().split())))

    adj = [[] for _ in range(n)]
    for u, v in edges:
        adj[u-1].append(v-1)
        adj[v-1].append(u-1)

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def find_gcds(u, v):
        if u == v:
            return x[u-1]

        if u > v:
            u, v = v, u

        result = x[u-1]
        for neighbor in adj[u]:
            if neighbor != v:
                result = gcd(result, find_gcds(neighbor, v))
        return result

    total_sum = 0
    for i in range(1, n):
        total_sum = (total_sum + find_gcds(0, i)) % (10**9 + 7)

    print(total_sum)


