        for i in range(2):
            for j in range(2):
                if grid[i][j] != 'X' and grid[i][j] != empty[0] and grid[i][j] != empty[1]:
                    new_grid = [row[:] for row in grid]
                    new_grid[empty[0]][empty[1]] = new_grid[i][j]
                    new_grid[i][j] = 'X'
                    new_moves = find_moves(new_grid, empty, moves + [(i, j)])
                    if new_moves:
                        return new_moves
        return None

    bessie_moves = find_moves(bessie_grid, bessie_empty, [])
    elsie_moves = find_moves(elsie_grid, elsie_empty, [])

