    def is_valid_move(current_square, move):
        new_square = current_square + move
        if new_square > n:
            return False
        if s[new_square] == '1':
            return False
        return True

    def find_shortest_sequence(start_square):
        queue = [(start_square, [])]
        visited = {start_square}

        while queue:
            current_square, path = queue.pop(0)

            if current_square == n:
                return path

            for move in range(1, m + 1):
                if is_valid_move(current_square, move):
                    new_square = current_square + move
                    if new_square not in visited:
                        visited.add(new_square)
                        queue.append((new_square, path + [move]))

        return None

    shortest_sequence = None
    for start_square in range(n + 1):
        sequence = find_shortest_sequence(start_square)
        if sequence:
            if shortest_sequence is None or len(sequence) < len(shortest_sequence):
                shortest_sequence = sequence
            elif len(sequence) == len(shortest_sequence) and sequence < shortest_sequence:
                shortest_sequence = sequence

    if shortest_sequence:
        print(*shortest_sequence)
    else:
        print(-1)


