def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    adj = [[] for _ in range(n + 1)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    def find_path(start, end, path):
        if start == end:
            return path
        for neighbor in adj[start]:
            if neighbor not in path:
                new_path = find_path(neighbor, end, path + [neighbor])
                if new_path:
                    return new_path
        return None

    def find_common_path(start1, end1, start2, end2):
        path1 = find_path(start1, end1, [])
        path2 = find_path(start2, end2, [])

        common = []
        for x in path1:
            if x in path2:
                common.append(x)
        return common

    def find_max_common_and_length(start1, end1, start2, end2):
        common = find_common_path(start1, end1, start2, end2)
        if not common:
            return 0, []

        max_common = 0
        max_common_path = []

        for i in range(1 << len(common)):
            subset = []
            for j in range(len(common)):
                if (i >> j) & 1:
                    subset.append(common[j])

            if len(subset) >= 2:
                subset_path = find_path(start1, end1, subset)
                if subset_path:
                    length = len(subset_path)
                    if length > max_common:
                        max_common = length
                        max_common_path = subset_path

        return max_common, max_common_path

    max_common, max_common_path = find_max_common_and_length(1, n, 2, n)

    print(max_common_path[0], max_common_path[-1])


