def solve():
    n = int(input())
    frogs = []
    for _ in range(n):
        x, d = map(int, input().split())
        frogs.append((x, d))

    def is_prime(n):
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True

    def find_smallest_position(frogs):
        positions = []
        for x, d in frogs:
            positions.append(x)
            if not is_prime(d):
                continue
            for i in range(2, int(d**0.5) + 1):
                if d % i == 0:
                    break
            else:
                positions.append(x + d)

        positions.sort()
        return positions[0]

    def find_highest_tower(positions):
        max_tower = 0
        for i in range(1 << len(positions)):
            tower = 0
            for j in range(len(positions)):
                if (i >> j) & 1:
                    tower += positions[j]
            max_tower = max(max_tower, tower)
        return max_tower

    smallest_position = find_smallest_position(frogs)
    highest_tower = find_highest_tower(frogs)

    print(smallest_position, highest_tower)


