    def find_path(start, end, visited, path):
        if start == end:
            return path

        visited[start] = True

        for neighbor, cost in adj[start]:
            if not visited[neighbor]:
                result = find_path(neighbor, end, visited, path + [(start, neighbor, cost)])
                if result:
                    return result

        return None

    min_cost = float('inf')

    for start_city in train_lines:
        for end_city in train_lines:
            if start_city != end_city:
                visited = {city: False for city in cities}
