    def calculate_coordinates(edges):
        coordinates = {}
        for i in range(1, n + 1):
            coordinates[i] = (0, 0)

        def calculate_coordinates_recursive(edges, current_node, parent_node):
            for edge in edges:
                if edge[0] != parent_node and edge[1] != parent_node:
                    calculate_coordinates_recursive(edges, edge[0], current_node)
                    calculate_coordinates_recursive(edges, edge[1], current_node)

            if current_node == 1:
                coordinates[current_node] = (0, 0)
            else:
                coordinates[current_node] = (coordinates[parent_node][0] + 1,
                                             coordinates[parent_node][1] + 1)

        calculate_coordinates_recursive(edges, 1, 1)
        return coordinates

    coordinates = calculate_coordinates(edges)

    for i in range(1, n + 1):
        print(coordinates[i][0], coordinates[i][1])


