        touch_points = []
        for i in range(15):
            for j in range(30):
                if image[i][j] == 'X':
                    touch_points.append((i, j))
        if not touch_points:
            return None

        grip_x = sum(i for i, j in touch_points) / len(touch_points)
        grip_y = sum(j for i, j in touch_points) / len(touch_points)

        return (grip_x, grip_y)

    def calculate_touch_vector(point, grip):
        return (point[0] - grip[0], point[1] - grip[1])

    def calculate_distance(vector1, vector2):
        return (vector1[0] - vector2[0])**2 + (vector1[1] - vector2[1])**2

    def calculate_grip_spread(grip):
        touch_points = []
        for i in range(15):
            for j in range(30):
                if image[i][j] == 'X':
                    touch_points.append((i, j))

        if not touch_points:
            return 0

        total_distance = 0
        for point in touch_points:
            vector = calculate_touch_vector(point, grip)
            total_distance += calculate_distance(vector, (0, 0))

        return total_distance / len(touch_points)

    def calculate_rotation_distance(grip1, grip2):
        touch_points1 = []
        for i in range(15):
            for j in range(30):
                if image1[i][j] == 'X':
                    touch_points1.append((i, j))

        touch_points2 = []
        for i in range(15):
            for j in range(30):
                if image2[i][j] == 'X':
                    touch_points2.append((i, j))

        if not touch_points1 or not touch_points2:
            return 0

        total_distance = 0
        for i in range(len(touch_points1)):
            vector1 = calculate_touch_vector(touch_points1[i], grip1)
            vector2 = calculate_touch_vector(touch_points2[i], grip2)
            total_distance += calculate_distance(vector1, vector2)

        return total_distance / len(touch_points1)

    def calculate_rotation(grip1, grip2):
        touch_points1 = []
        for i in range(15):
            for j in range(30):
                if image1[i][j] == 'X':
                    touch_points1.append((i, j))

        touch_points2 = []
        for i in range(15):
            for j in range(30):
                if image2[i][j] == 'X':
                    touch_points2.append((i, j))

        if not touch_points1 or not touch_points2:
            return 0

        total_distance = 0
        for i in range(len(touch_points1)):
            vector1 = calculate_touch_vector(touch_points1[i], grip1)
            vector2 = calculate_touch_vector(touch_points2[i], grip2)
            total_distance += calculate_distance(vector1, vector2)

        return total_distance / len(touch_points1)

    grip1 = calculate_grip_point(image1)
    grip2 = calculate_grip_point(image2)

    if grip1 is None or grip2 is None:
        print('1 pan')
        return

    pan_distance = calculate_distance(grip1, grip2)
    grip_spread1 = calculate_grip_spread(grip1)
    grip_spread2 = calculate_grip_spread(grip2)

    zoom_distance = abs(grip_spread1 - grip_spread2)
    rotation_distance = calculate_rotation_distance(grip1, grip2)

