def solve():
    r, c = map(int, input().split())
    bed, fridge = map(int, input().split())
    bed -= 1
    fridge -= 1

    grid = []
    for _ in range(r):
        grid.append(input())

    p = int(input())
    masters = []
    for _ in range(p):
        masters.append(tuple(map(int, input().split())))

    def is_valid_move(x, y):
        return 0 <= x < r and 0 <= y < c and grid[x][y] == '.'

    def calculate_distance(start, end):
        queue = [(start, 0)]
        visited = {start}

        while queue:
            (x, y), dist = queue.pop(0)

            if (x, y) == end:
                return dist

            for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                nx, ny = x + dx, y + dy

                if is_valid_move(nx, ny) and (nx, ny) not in visited:
                    queue.append(((nx, ny), dist + 1))
                    visited.add((nx, ny))

        return float('inf')

    distance_to_bed = calculate_distance((0, 0), bed)
    distance_to_fridge = calculate_distance((0, 0), fridge)

    min_distance = min(distance_to_bed, distance_to_fridge)

    for master in masters:
        distance_to_master = calculate_distance((0, 0), master)

        if distance_to_master <= min_distance:
            min_distance = distance_to_master

    if min_distance == float('inf'):
        print('IMPOSSIBLE')
    else:
        print(min_distance)


solve()

