            steps = 0
            current_word = start_word
            while current_word != end_word:
                steps += 1
                current_word = get_next_word(current_word, word)
            if steps < min_steps:
                min_steps = steps
                solution_word = word
            elif steps == min_steps:
                solution_word = min(solution_word, word)
        else:
            steps = 0
            current_word = start_word
            while current_word != word:
                steps += 1
                current_word = get_next_word(current_word, word)
            if steps < min_steps:
                min_steps = steps
                solution_word = word
            elif steps == min_steps:
                solution_word = min(solution_word, word)

    if solution_word == '':
        print(0)
        print(-1)
    else:
        print(solution_word)
        print(min_steps)


def get_next_word(current_word, target_word):
    diff = ''
    for i in range(len(current_word)):
        if current_word[i] != target_word[i]:
            diff += target_word[i]
    return current_word[:-1] + diff


