def solve():
    n = int(input())
    edges = []
    for _ in range(2 * (n - 1)):
        u, v = map(int, input().split())
        edges.append((u, v))

    def is_valid_assignment(assignment):
        left_tree = []
        right_tree = []

        for i in range(len(edges)):
            if assignment[i] == 'L':
                left_tree.append(edges[i])
            else:
                right_tree.append(edges[i])

        left_tree_edges = []
        right_tree_edges = []

        for edge in left_tree:
            left_tree_edges.append(edge)

        for edge in right_tree:
            right_tree_edges.append(edge)

        left_tree_edges.sort(key=lambda x: x[0])
        right_tree_edges.sort(key=lambda x: x[1])

        left_tree_edges_str = []
        for edge in left_tree_edges:
            left_tree_edges_str.append(str(edge[0]) + str(edge[1]))

        right_tree_edges_str = []
        for edge in right_tree_edges:
            right_tree_edges_str.append(str(edge[1]) + str(edge[0]))

        left_tree_edges_str.sort()
        right_tree_edges_str.sort()

        if left_tree_edges_str == left_tree_edges_str[::-1] and \
           right_tree_edges_str == right_tree_edges_str[::-1]:
            return True
        else:
            return False

    def find_assignment(edges):
        for i in range(1 << len(edges)):
            assignment = []
            for j in range(len(edges)):
                if (i >> j) & 1:
                    assignment.append('L')
                else:
                    assignment.append('R')

            if is_valid_assignment(assignment):
                return assignment

    assignment = find_assignment(edges)

    if assignment:
        print(''.join(assignment))
    else:
        print('impossible')


solve()

