    n, k = map(int, input().split())
    a = list(map(int, input().split()))

    adj = [[] for _ in range(n + 1)]
    for i in range(n):
        adj[a[i]].append(i + 1)

    def dfs(node, visited, path):
        if len(path) == n:
            if len(visited) == n:
                return path
            return None

        for neighbor in adj[node]:
            if neighbor not in visited:
                new_visited = visited.copy()
                new_visited.add(neighbor)
                result = dfs(neighbor, new_visited, path + [neighbor])
                if result:
                    return result
        return None

    for start_node in range(1, n + 1):
        path = dfs(start_node, {start_node}, [start_node])
        if path:
            if len(path) == n:
                if len(path) == n:
                    print(*path)
                    return
                else:
                    print('Impossible')
                    return

