            x, y = packages[i]
            remaining_packages = packages[:i] + packages[i+1:]
            for path in generate_paths(remaining_packages):
                paths.append('R' + path + 'U')
        return paths

    paths = generate_paths(packages)

    min_distance = float('inf')
    shortest_path = ''

    for path in paths:
        distance = calculate_distance(packages)
        if distance < min_distance:
            min_distance = distance
            shortest_path = path
        elif distance == min_distance and path < shortest_path:
            shortest_path = path

    if shortest_path == '':
        print('NO')
    else:
        print('YES')
        print(shortest_path)


