def solve():
    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input())

    def is_valid(x, y):
        if x < 0 or y < 0 or x >= n or y >= m:
            return False
        return grid[x][y] == '.'

    def check_adjacent(x, y):
        adj = []
        if x > 0:
            adj.append((x - 1, y))
        if x < n - 1:
            adj.append((x + 1, y))
        if y > 0:
            adj.append((x, y - 1))
        if y < m - 1:
            adj.append((x, y + 1))
        return adj

    def solve_recursive(x, y):
        if x == n and y == m:
            return True

        adj = check_adjacent(x, y)
        for i in range(len(adj)):
            nx, ny = adj[i]
            if is_valid(nx, ny) and not grid[nx][ny] == 'E':
                grid[nx][ny] = 'E'
                if solve_recursive(nx, ny):
                    return True
                grid[nx][ny] = '.'
        return False

    solve_recursive(0, 0)

    for row in grid:
        print(''.join(row))


