def solve():
    n, m, k = map(int, input().split())
    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    dp = {}

    def calculate_paths(i, j, xor_sum):
        if (i, j, xor_sum) in dp:
            return dp[(i, j, xor_sum)]

        if i == n and j == m:
            return 1 if xor_sum == k else 0

        if i >= n or j >= m:
            return 0

        paths = 0
        if i < n:
            paths += calculate_paths(i + 1, j, xor_sum ^ a[i][j])
        if j < m:
            paths += calculate_paths(i, j + 1, xor_sum ^ a[i][j])

        dp[(i, j, xor_sum)] = paths
        return paths

    print(calculate_paths(0, 0, 0))


solve()

def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))

    adj = [[] for _ in range(n + 1)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    def dfs(node, visited, path):
        visited[node] = True
        path.append(node)
        for neighbor in adj[node]:
            if not visited[neighbor]:
                dfs(neighbor, visited, path)
            elif neighbor in path:
                return False
        return True

    visited = [False] * (n + 1)
    path = []
    if not dfs(1, visited, path):
        print('NO')
        return

    print('YES')
    orientation = ['0'] * m
    for i in range(m):
        u, v = edges[i]
        if path[i] == u and path[i+1] == v:
            orientation[i] = '1'
        elif path[i] == v and path[i+1] == u:
            orientation[i] = '0'
        else:
            orientation[i] = '1'

    print(''.join(orientation))


solve()

