def solve():
    a, n, m = map(int, input().split())
    segments = []
    for _ in range(n):
        l, r = map(int, input().split())
        segments.append((l, r))

    umbrellas = []
    for _ in range(m):
        x, p = map(int, input().split())
        umbrellas.append((x, p))

    dp = {}

    def calculate_fatigue(umbrellas, current_x):
        if current_x == a:
            return 0

        if (current_x, tuple(umbrellas)) in dp:
            return dp[(current_x, tuple(umbrellas))]

        min_fatigue = float('inf')

        for i in range(len(umbrellas)):
            new_umbrellas = umbrellas[:i] + umbrellas[i+1:]
            new_x = current_x + 1

            if new_x > a:
                continue

            if not any(segment[0] <= new_x < segment[1] for segment in segments):
                min_fatigue = min(min_fatigue, calculate_fatigue(new_umbrellas, new_x))
            else:
                min_fatigue = min(min_fatigue, calculate_fatigue(new_umbrellas, new_x) + umbrellas[i][1])

        dp[(current_x, tuple(umbrellas))] = min_fatigue
        return min_fatigue

    print(calculate_fatigue(umbrellas, 0))


solve()

