def solve():
    n, k = map(int, input().split())
    s = input()

    dp = {}

    def calculate_cost(subsequence):
        if subsequence in dp:
            return dp[subsequence]

        cost = 0
        for char in subsequence:
            cost += 1

        dp[subsequence] = cost
        return cost

    def find_subsequences(length):
        if length == 0:
            return [[]]

        subsequences = []
        for i in range(length):
            for sub in find_subsequences(length - i - 1):
                subsequences.append(sub + [s[i]])

        return subsequences

    subsequences = find_subsequences(n)

    min_cost = float('inf')

    for sub in subsequences:
        cost = calculate_cost(sub)

        if cost <= k:
            min_cost = min(min_cost, cost)

    if min_cost == float('inf'):
        print('-1')
    else:
        print(min_cost)


solve()

