                min_leaf_value = min(min_leaf_value, v)
            else:
                min_leaf_value = min(min_leaf_value, find_min_leaf_value(edges[:i] + edges[i+1:]))
        return min_leaf_value

    edges.sort(key=lambda x: x[1])
    max_leaf_value = find_max_leaf_value(edges)
    min_leaf_value = find_min_leaf_value(edges)

