def mirror(a):
    x = len(a)
    y = len(a[0])
    b = [[0] * y for _ in range(2 * x)]
    for i in range(x):
        for j in range(y):
            b[i][j] = a[i][j]
            b[x + i][y - 1 - j] = a[i][j]
    return b

def is_symmetric(a):
    x = len(a)
    y = len(a[0])
    for i in range(x // 2):
        for j in range(y):
            if a[i][j] != a[x - 1 - i][y - 1 - j]:
                return False
    return True

